mapper.insert:
INSERT INTO purchase_order (
    order_no, supplier_id, status, total_amount, remark, creator, create_time, update_time
) VALUES (
    #{orderNo}, #{supplierId}, #{status}, #{totalAmount}, #{remark}, #{creator}, NOW(), NOW()
)

mapper.select:
SELECT p.*, s.name AS supplier_name FROM purchase_order p LEFT JOIN supplier s ON p.supplier_id = s.id
AND p.order_no LIKE CONCAT('%', #{orderNo}, '%')
AND p.status = #{status}
AND p.supplier_id = #{supplierId}
ORDER BY p.create_time DESC
