mapper.select:
SELECT id, username, password, nickname, role, status
FROM sys_user
WHERE id = #{id}

SELECT id, username, password, nickname, role, status
FROM sys_user
WHERE username = #{username}

mapper.update:
UPDATE sys_user
SET nickname = #{nickname},
    role = #{role},
    status = #{status}
WHERE id = #{id}

UPDATE sys_user
SET status = #{status}
WHERE id = #{id}

UPDATE sys_user
SET password = #{password}
WHERE id = #{id}
